# Fake Chrome Update Box

Add-Type -AssemblyName System.Windows.Forms
Add-Type -AssemblyName System.Drawing

# Set DPI Awareness
Add-Type @"
using System;
using System.Runtime.InteropServices;
public class DpiAware {
    [DllImport("user32.dll")]
    public static extern bool SetProcessDPIAware();
}
"@
[Void][DpiAware]::SetProcessDPIAware()

# Base64 to Image function
function ConvertFrom-Base64ToImage {
    param (
        [string]$Base64String
    )
    $imageBytes = [Convert]::FromBase64String($Base64String)
    $ms = New-Object System.IO.MemoryStream
    $ms.Write($imageBytes, 0, $imageBytes.Length)
    $ms.Position = 0
    return [System.Drawing.Image]::FromStream($ms)
}

# Chrome image Base64 string
$base64Icon = ""
$icon = ConvertFrom-Base64ToImage -Base64String $base64Icon

# Form itself
$form = New-Object System.Windows.Forms.Form
$form.Text = "Updating Chrome..."
$form.Size = New-Object System.Drawing.Size(750,160)
$form.StartPosition = "CenterScreen"
$form.Icon = [System.Drawing.Icon]::FromHandle($icon.GetHicon())

# Progress bar
$progressbar = New-Object System.Windows.Forms.ProgressBar
$progressbar.Minimum = 0
$progressbar.Maximum = 100
$progressbar.Location = New-Object System.Drawing.Size(16,40)
$progressbar.Size = New-Object System.Drawing.Size(700,40)
$progressbar.ForeColor = [System.Drawing.Color]::Green
$progressbar.Style = [System.Windows.Forms.ProgressBarStyle]::Continuous

# Progress percentage 
$progresslabel = New-Object System.Windows.Forms.Label
$progresslabel.Location = New-Object System.Drawing.Point(10, 15)
$progresslabel.Size = New-Object System.Drawing.Size(700, 20)
$progresslabel.Text = "0% Complete"
$progresslabel.TextAlign = [System.Drawing.ContentAlignment]::MiddleCenter

# Add controls
$form.Controls.Add($progresslabel)
$form.Controls.Add($progressbar)

# Loading bar variables
$data = 1..100
$index = 1

# Show form
$form.Show()

# Loading bar loop
foreach ($item in $data) {
   
   $progressprecent = ($index / $data.Count) * 100
   $progressbar.Value = $progressprecent
   $progresslabel.Text = "$progressprecent% Complete"

   # Delay added
   Start-Sleep -Milliseconds 100
   $index++

   # If it has reached 100% 
   if ($index -eq 101) { 
        $form.Text = "Chrome updated successfully"
        Start-Sleep -Milliseconds 500
   }
}

# Close form
$form.Close()